		WHEN 'P' THEN 'Person Record'
	END AS "Record Type",
	CASE SERIALNO
		WHEN '2022GQ0000001' THEN 'GQ Unique identifier'
		WHEN '2022HU0000001' THEN 'HU Unique identifier'
	END AS "Housing unit/GQ person serial number",
